mapper.select:
SELECT bookid AS bookId , bookname AS bookName , publisher , price FROM book
WHERE bookname LIKE '%${searchText}%'
ORDER BY bookname, bookid LIMIT #{startIdx}, #{size}

mapper.update:
UPDATE book


        bookname = #{bookName}


        , publisher = #{publisher}


        , price = #{price}


WHERE bookid = #{bookId}
